var scrollCheck = false;
var cbpAnimatedHeader = (function() {

    var docElem = document.documentElement,

        fixedMenu = document.querySelector('.fixed'),
        menuFontSize = document.querySelector('.mainmenu li a');



    didScroll = false,
        changeHeaderOn = 20;

    function init() {
        window.addEventListener('scroll', function(event) {
            if (!didScroll) {
                didScroll = true;
                scrollCheck = false;
                setTimeout(scrollPage, 150);

            }
        }, false);
    }

    function scrollPage() {

        var sy = scrollY();

        if (sy >= changeHeaderOn) {
            classie.add(fixedMenu, 'fixed-con');
            //classie.add(menuFontSize, 'font-small');
            $('.mainmenu li a').addClass('font-small')

        } else {


            classie.remove(fixedMenu, 'fixed-con');
            $('.mainmenu li a').removeClass('font-small')




        }
        didScroll = false;
    }

    function scrollY() {
        return window.pageYOffset || docElem.scrollTop;
    }

    init();

})();